<script setup>
    import gsap from 'gsap'

    const props = defineProps({
        showNavi: {
            type: Boolean,
            default: false
        }
    })
    const header = ref(null)

    function showNaviAni() {
        gsap.to(header.value, 
        {
            yPercent: 0,
            ease:'power2.easeInOut',
            duration: 0.4,
            delay: 0.8
        })
    }    

    watch(
        () => props.showNavi,
        () => {
            showNaviAni()
        }
    )
    onMounted(() => {
        gsap.set(header.value,{
            yPercent: -100
        })
    })

    
</script>
